// src/validator/validator.ts
import { parseBody } from "../utils/body.js";
var validator = (target, validationFunc) => {
  return async (c, next) => {
    let value = {};
    switch (target) {
      case "json":
        try {
          value = await c.req.raw.clone().json();
        } catch {
          console.error("Error: Malformed JSON in request body");
          return c.json(
            {
              success: false,
              message: "Malformed JSON in request body"
            },
            400
          );
        }
        break;
      case "form":
        value = await parseBody(c.req.raw.clone());
        break;
      case "query":
        value = c.req.query();
        break;
      case "queries":
        value = c.req.queries();
        break;
    }
    const res = validationFunc(value, c);
    if (res instanceof Response || res instanceof Promise) {
      return res;
    }
    c.req.addValidatedData(target, res);
    await next();
  };
};
export {
  validator
};
